__NUXT_JSONP__("/drugs/Adagloxad_Simolenin", (function(a,b,c,d){return {data:[{drug:{slug:a,emaEpar:[],fdaDrugLabel:[],id:a,nciThesaurus:{casRegistry:"1458687-85-8",chebiId:b,chemicalFormula:b,definition:"A carbohydrate-based immunostimulant comprised of the Globo H hexasaccharide 1 (Globo H) epitope linked to the immunostimulant carrier protein keyhole limpet hemocyanin (KLH), with potential antineoplastic activity. Upon administration of adagloxad simolenin, the carbohydrate antigen Globo H may stimulate a cytotoxic T-lymphocyte (CTL) response against Globo H-expressing tumor cells, thereby decreasing tumor cell proliferation. Globo H is a tumor associated antigen (TAA) commonly found on a variety of tumor cells including breast cancer cells. KLH improves antigenic immune recognition and T-cell responses.",fdaUniiCode:"UCG949AA7Z",identifier:"C99766",preferredName:c,semanticType:"Pharmacologic Substance",subclassOf:["C2139"],synonyms:["ADAGLOXAD SIMOLENIN",c,"OBI 822","OBI-822","OBI822"]},dir:"\u002Fdrugs",path:"\u002Fdrugs\u002FAdagloxad_Simolenin",extension:".json",createdAt:d,updatedAt:d}}],fetch:{},mutations:void 0}}("Adagloxad_Simolenin","","Adagloxad Simolenin","2021-10-30T13:49:06.623Z")));