__NUXT_JSONP__("/drugs/Angiopoietin-2-specific_Fusion_Protein_PF-04856884", (function(a,b,c,d){return {data:[{drug:{slug:b,emaEpar:[],fdaDrugLabel:[],id:b,nciThesaurus:{casRegistry:a,chebiId:a,chemicalFormula:a,definition:"A humanized monoclonal antibody fused to two peptides that bind to angiopoietin 2 (Ang2; ANGPT2), with potential anti-angiogenic and antineoplastic activities. Upon intravenous administration, Ang2-targeting PF-04856884 CovX body specifically binds to Ang2 and prevents the binding of Ang2 to its receptor Tie2 expressed on endothelial cells. This inhibits Tie2-mediated signaling, prevents angiogenesis and inhibits tumor cell proliferation.  Ang2, a proangiogenic cytokine and ligand for the Tie2 receptor, plays a key role in the regulation of tumor angiogenesis and tumor cell proliferation; its expression is upregulated by tumor endothelial cells.",fdaUniiCode:"78LXD26OX3",identifier:"C116625",preferredName:c,semanticType:"Pharmacologic Substance",subclassOf:["C1512","C1742"],synonyms:[c,"CVX-060","PF-04856884"]},dir:"\u002Fdrugs",path:"\u002Fdrugs\u002FAngiopoietin-2-specific_Fusion_Protein_PF-04856884",extension:".json",createdAt:d,updatedAt:d}}],fetch:{},mutations:void 0}}("","Angiopoietin-2-specific_Fusion_Protein_PF-04856884","Angiopoietin-2-specific Fusion Protein PF-04856884","2021-10-30T13:19:10.003Z")));