__NUXT_JSONP__("/drugs/Anti-IGF-1R_Monoclonal_Antibody_AVE1642", (function(a,b,c,d){return {data:[{drug:{slug:a,emaEpar:[],fdaDrugLabel:[],id:a,nciThesaurus:{casRegistry:"1242567-05-0",chebiId:b,chemicalFormula:b,definition:"A humanized monoclonal antibody directed against the human insulin-like growth factor-1 receptor (IGF-1R\u002FCD221) with potential antineoplastic activity. Anti-IGF-1R monoclonal antibody AVE1642 specifically binds to and blocks membrane-bound IGF-1R, preventing the binding of the natural ligand IGF-1 and the subsequent activation of PI3K\u002FAKT signal transduction, which may result in the induction of apoptosis and a decrease in cellular proliferation. Activation of IGF-1R, a receptor tyrosine kinase of the insulin receptor superfamily overexpressed by various cancer cell types, stimulates cell proliferation, promotes angiogenesis, enables oncogenic transformation, and suppresses apoptosis.",fdaUniiCode:"Z9U2SS9ZC8",identifier:"C79798",preferredName:c,semanticType:"Amino Acid, Peptide, or Protein",subclassOf:["C129822","C182012"],synonyms:["AVE-1642","AVE1642",c,"anti-CD122 monoclonal antibody AVE1642"]},dir:"\u002Fdrugs",path:"\u002Fdrugs\u002FAnti-IGF-1R_Monoclonal_Antibody_AVE1642",extension:".json",createdAt:d,updatedAt:d}}],fetch:{},mutations:void 0}}("Anti-IGF-1R_Monoclonal_Antibody_AVE1642","","Anti-IGF-1R Monoclonal Antibody AVE1642","2021-10-30T13:45:24.133Z")));