__NUXT_JSONP__("/drugs/Obatoclax_Mesylate", (function(a,b,c){return {data:[{drug:{slug:a,emaEpar:[],fdaDrugLabel:[],id:a,nciThesaurus:{casRegistry:"803712-79-0",chebiId:"",chemicalFormula:"C20H19N3O.CH4O3S",definition:"The mesylate salt of obatoclax, a synthetic small-molecule inhibitor of the bcl-2 family of proteins with potential pro-apoptotic and antineoplastic activities. Obatoclax binds to members of the Bcl-2 protein family, preventing the binding of these anti-apoptotic proteins to the pro-apoptotic proteins Bax and Bak and so promoting the activation of the apoptotic pathway in Bcl-2-overexpressing cells. The Bcl-2 family of proteins (bcl-2, bcl-xl, bcl-w, and Mcl-1) are overexpressed in a wide variety of cancers, including those of the lymphatic system, breast, lung, prostate, and colon.",fdaUniiCode:"39200FJ43J",identifier:"C62771",preferredName:b,semanticType:"Pharmacologic Substance",subclassOf:["C129824"],synonyms:["2-(2-[(3,5-Dimethyl-1H-pyrrol-2-yl)methylidene]-3-methoxy-2H-pyrrol-5-yl)-1H-indole Monomethanesulfonate","GX15-070MS","OBATOCLAX MESYLATE",b,"obatoclax mesylate"]},dir:"\u002Fdrugs",path:"\u002Fdrugs\u002FObatoclax_Mesylate",extension:".json",createdAt:c,updatedAt:c}}],fetch:{},mutations:void 0}}("Obatoclax_Mesylate","Obatoclax Mesylate","2021-10-30T13:42:18.043Z")));