__NUXT_JSONP__("/drugs/Oportuzumab_Monatox", (function(a,b,c,d){return {data:[{drug:{slug:a,emaEpar:[],fdaDrugLabel:[],id:a,nciThesaurus:{casRegistry:"945228-48-8",chebiId:b,chemicalFormula:b,definition:"A fusion protein immunotoxin consisting of a humanized, single-chain monoclonal antibody fragment specific for the epithelial cell adhesion molecule (EpCAM) conjugated with a truncated form of Pseudomonas exotoxin A with potential antineoplastic activity. Oportuzumab monatox binds to Ep-CAM-positive tumor cells, thereby delivering the Pseudomonas exotoxin A moiety specifically; the Pseudomonas exotoxin A moiety then inactivates elongation factor 2 (EF-2) through ADP ribosylation, resulting in inhibition of protein synthesis in target cells. EpCAM, a cell surface protein, is expressed by a variety of tumor cells and is frequently found in head and neck cancers.",fdaUniiCode:"945CY7ZMI2",identifier:"C61307",preferredName:c,semanticType:"Pharmacologic Substance",subclassOf:["C575"],synonyms:["OPORTUZUMAB MONATOX",c,"Proxinium","VB4 845","VB4-845","Vicinium","anti-EpCAM-Pseudomonas-exotoxin fusion protein"]},dir:"\u002Fdrugs",path:"\u002Fdrugs\u002FOportuzumab_Monatox",extension:".json",createdAt:d,updatedAt:d}}],fetch:{},mutations:void 0}}("Oportuzumab_Monatox","","Oportuzumab Monatox","2021-10-30T13:41:23.953Z")));