__NUXT_JSONP__("/drugs/Puquitinib", (function(a,b,c){return {data:[{drug:{slug:a,emaEpar:[],fdaDrugLabel:[],id:a,nciThesaurus:{casRegistry:"916890-10-3",chebiId:b,chemicalFormula:b,definition:"An orally available selective inhibitor of the delta form of phosphatidylinositol 3-kinase (PI3-kinase subunit delta; PI3K-delta; PI3Kdelta), with potential antineoplastic activity. Upon oral administration, puquitinib selectively binds to the ATP-binding pocket of PI3K-delta and prevents the activation of the PI3K\u002FAKT signaling pathway. This decreases proliferation of and induces cell death in PI3K-delta over-expressing tumor cells. PI3K-delta also plays a key role in the B-cell receptor (BCR) signaling pathway and the proliferation of certain hematologic cancer cells. The targeted inhibition of PI3K-delta is designed to preserve PI3K signaling in normal, non-neoplastic cells, thereby minimizing serious side effects.",fdaUniiCode:"WFG29AB872",identifier:"C162555",preferredName:a,semanticType:"Pharmacologic Substance",subclassOf:["C129825","C2152"],synonyms:["PUQUITINIB",a,"XC-302 Free Base"]},dir:"\u002Fdrugs",path:"\u002Fdrugs\u002FPuquitinib",extension:".json",createdAt:c,updatedAt:c}}],fetch:{},mutations:void 0}}("Puquitinib","","2021-10-30T13:35:25.663Z")));